import React, { useState, useEffect } from 'react';
import './App.css';
import RouteList from './components/Route/RouteList';
import BusList from './components/Bus/BusList';
import SeatSelection from './components/Booking/SeatSelection';
import BookingForm from './components/Booking/BookingForm';
import PaymentForm from './components/Payment/PaymentForm';
import PaymentSuccess from './components/Payment/PaymentSuccess';
import MyBookings from './components/Booking/MyBookings';
import BookingDetailsModal from './components/Booking/BookingDetailsModal';

function App() {
  const [routes, setRoutes] = useState([]);
  const [buses, setBuses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState('home');
  const [selectedRoute, setSelectedRoute] = useState(null);
  const [selectedBus, setSelectedBus] = useState(null);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [passengerDetails, setPassengerDetails] = useState(null);
  const [bookingId, setBookingId] = useState('');
  const [selectedBookingForDetails, setSelectedBookingForDetails] = useState(null);

  useEffect(() => {
    fetchRoutes();
  }, []);

  const fetchRoutes = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/routes');
      const data = await response.json();
      setRoutes(data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching routes:', error);
      setLoading(false);
    }
  };

  const fetchBusesByRoute = async (routeId) => {
    try {
      const response = await fetch('http://localhost:5000/api/buses');
      const allBuses = await response.json();
      const filteredBuses = allBuses.filter(bus => bus.routeId._id === routeId);
      setBuses(filteredBuses);
    } catch (error) {
      console.error('Error fetching buses:', error);
    }
  };

  const handleRouteSelect = (route) => {
    setSelectedRoute(route);
    fetchBusesByRoute(route._id);
    setCurrentPage('buses');
  };

  const handleBusSelect = (bus) => {
    setSelectedBus(bus);
    setCurrentPage('seats');
  };

  const handleSeatSelect = (seats) => {
    setSelectedSeats(seats);
    setCurrentPage('booking');
  };

  const handleBookingComplete = async (details) => {
    setPassengerDetails(details);

    // Calculate total based on selected seats and their types
    const totalAmount = selectedSeats.reduce((total, seatNum) => {
      const seatConfig = selectedBus.seatConfiguration.find(s => s.seatNumber === seatNum);
      return total + (seatConfig ? seatConfig.price : 150);
    }, 0);

    // Create booking first
    try {
      const bookingData = {
        busId: selectedBus._id,
        routeId: selectedRoute._id,
        seats: selectedSeats,
        passengerName: details.name,
        passengerEmail: details.email,
        passengerPhone: details.phone,
        totalAmount: totalAmount
      };

      const response = await fetch('http://localhost:5000/api/bookings', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(bookingData)
      });

      const result = await response.json();

      if (result._id) {
        setBookingId(result._id);
        setCurrentPage('payment');
      } else {
        alert('Error creating booking');
      }
    } catch (error) {
      console.error('Error creating booking:', error);
      alert('Error creating booking');
    }
  };

  const handleGoHome = () => {
    setCurrentPage('home');
    setSelectedRoute(null);
    setSelectedBus(null);
    setSelectedSeats([]);
    setPassengerDetails(null);
    setBookingId('');
  };

  const renderHome = () => {
    return (
      <div className="home-container">
        <div className="hero-section">
          <h1>üöå Zambian Bus Booking System</h1>
          <p>Book your bus tickets easily and travel comfortably across Zambia</p>
          <div className="nav-buttons">
            <button onClick={() => setCurrentPage('myBookings')} className="btn btn-outline">
              üìã My Bookings
            </button>
          </div>
        </div>
        <RouteList routes={routes} onSelectRoute={handleRouteSelect} loading={loading} />
      </div>
    );
  };

  const renderBuses = () => {
    return (
      <div className="buses-container">
        <button onClick={handleGoHome} className="btn btn-outline">
          ‚Üê Back to Routes
        </button>
        <h2>Available Buses</h2>
        <p className="route-info">
          {selectedRoute.startLocation} ‚Üí {selectedRoute.destination}
        </p>
        <BusList buses={buses} onSelectBus={handleBusSelect} />
      </div>
    );
  };

  const renderSeats = () => {
    return (
      <SeatSelection
        busId={selectedBus._id}
        onSeatSelect={handleSeatSelect}
        onBack={() => setCurrentPage('buses')}
      />
    );
  };

  const renderBooking = () => {
    return (
      <BookingForm
        selectedRoute={selectedRoute}
        selectedBus={selectedBus}
        selectedSeats={selectedSeats}
        onComplete={handleBookingComplete}
      />
    );
  };

  const renderPayment = () => {
    const totalAmount = selectedSeats.reduce((total, seatNum) => {
      const seatConfig = selectedBus.seatConfiguration.find(s => s.seatNumber === seatNum);
      return total + (seatConfig ? seatConfig.price : 150);
    }, 0);

    const bookingDetails = {
      bookingId: bookingId,
      totalAmount: totalAmount,
      route: `${selectedRoute.startLocation} ‚Üí ${selectedRoute.destination}`,
      busNumber: selectedBus.busNumber,
      seats: selectedSeats
    };

    return (
      <div className="payment-container">
        <h2>Complete Your Booking</h2>
        <div className="booking-summary">
          <p><strong>Route:</strong> {selectedRoute.startLocation} ‚Üí {selectedRoute.destination}</p>
          <p><strong>Bus:</strong> {selectedBus.busNumber}</p>
          <p><strong>Seats:</strong> {selectedSeats.join(', ')}</p>
          <p><strong>Total:</strong> K{totalAmount}</p>
        </div>
        <PaymentForm 
          bookingDetails={bookingDetails}
          onPaymentSuccess={(data) => {
            setCurrentPage('success');
          }}
        />
      </div>
    );
  };

  const renderSuccess = () => {
    return (
      <PaymentSuccess
        bookingDetails={{
          route: `${selectedRoute.startLocation} ‚Üí ${selectedRoute.destination}`,
          busNumber: selectedBus.busNumber,
          seats: selectedSeats,
          passengerName: passengerDetails?.name,
          email: passengerDetails?.email,
          bookingId: bookingId
        }}
        onGoHome={handleGoHome}
      />
    );
  };

  const renderMyBookings = () => {
    return (
      <>
        <button onClick={handleGoHome} className="btn btn-outline" style={{margin: '20px'}}>
          ‚Üê Back to Home
        </button>
        <MyBookings onViewDetails={setSelectedBookingForDetails} />
        {selectedBookingForDetails && (
          <BookingDetailsModal
            booking={selectedBookingForDetails}
            onClose={() => setSelectedBookingForDetails(null)}
            onPrint={(booking) => {
              window.print();
            }}
          />
        )}
      </>
    );
  };

  return (
    <div className="App">
      {currentPage === 'home' && renderHome()}
      {currentPage === 'buses' && renderBuses()}
      {currentPage === 'seats' && renderSeats()}
      {currentPage === 'booking' && renderBooking()}
      {currentPage === 'payment' && renderPayment()}
      {currentPage === 'success' && renderSuccess()}
      {currentPage === 'myBookings' && renderMyBookings()}
    </div>
  );
}

export default App;
