import React, { useState } from 'react';
import './App.css';
import Home from './pages/Home';
import BusSearch from './components/Booking/BusSearch';
import SeatSelection from './components/Booking/SeatSelection';
import BookingForm from './components/Booking/BookingForm';

function App() {
  const [currentPage, setCurrentPage] = useState('home');
  const [selectedRoute, setSelectedRoute] = useState(null);
  const [selectedBus, setSelectedBus] = useState(null);
  const [selectedSeats, setSelectedSeats] = useState([]);

  const handleRouteSelect = (route) => {
    setSelectedRoute(route);
    setCurrentPage('busSearch');
  };

  const handleBusSelect = (bus) => {
    setSelectedBus(bus);
    setCurrentPage('seatSelection');
  };

  const handleSeatSelect = (seats) => {
    setSelectedSeats(seats);
    setCurrentPage('booking');
  };

  const resetBooking = () => {
    setSelectedRoute(null);
    setSelectedBus(null);
    setSelectedSeats([]);
    setCurrentPage('home');
  };

  const renderPage = () => {
    switch (currentPage) {
      case 'busSearch':
        return (
          <BusSearch 
            selectedRoute={selectedRoute}
            onBusSelect={handleBusSelect}
            onBack={() => setCurrentPage('home')}
          />
        );
      case 'seatSelection':
        return (
          <SeatSelection 
            selectedBus={selectedBus}
            onSeatSelect={handleSeatSelect}
            onBack={() => setCurrentPage('busSearch')}
          />
        );
      case 'booking':
        return (
          <BookingForm 
            selectedRoute={selectedRoute}
            selectedBus={selectedBus}
            selectedSeats={selectedSeats}
            onBack={() => setCurrentPage('seatSelection')}
            onComplete={resetBooking}
          />
        );
      default:
        return <Home onRouteSelect={handleRouteSelect} setCurrentPage={setCurrentPage} />;
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <h1 onClick={() => setCurrentPage('home')} style={{cursor: 'pointer'}}>
            ðŸšŒ Zambian Bus Booking System
          </h1>
          <nav className="nav-buttons">
            <button onClick={() => setCurrentPage('home')} className="nav-btn">
              Home
            </button>
            <button onClick={() => setCurrentPage('home')} className="nav-btn">
              Book Tickets
            </button>
          </nav>
        </div>
      </header>
      <main>
        {renderPage()}
      </main>
    </div>
  );
}

export default App;
