import React, { useState } from 'react';
import axios from 'axios';
import './PaymentForm.css';

const PaymentForm = ({ bookingDetails, onPaymentSuccess }) => {
    const [paymentMethod, setPaymentMethod] = useState('mobileMoney');
    const [loading, setLoading] = useState(false);
    const [requiresPin, setRequiresPin] = useState(false);
    const [requiresOtp, setRequiresOtp] = useState(false);
    const [flwRef, setFlwRef] = useState('');

    // Mobile Money Form
    const [mobileMoneyData, setMobileMoneyData] = useState({
        phoneNumber: '',
        provider: 'MTN',
        name: '',
        email: ''
    });

    // Card Form
    const [cardData, setCardData] = useState({
        cardNumber: '',
        expiryMonth: '',
        expiryYear: '',
        cvv: '',
        name: '',
        email: '',
        pin: '',
        otp: ''
    });

    const handleMobileMoneyPayment = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            const response = await axios.post('http://localhost:5000/api/payments/mobile-money', {
                bookingId: bookingDetails.bookingId,
                phoneNumber: mobileMoneyData.phoneNumber,
                provider: mobileMoneyData.provider,
                amount: bookingDetails.totalAmount,
                email: mobileMoneyData.email,
                name: mobileMoneyData.name
            });

            if (response.data.success) {
                alert('Payment initiated! Please check your phone to complete the payment.');
                // Poll for payment status
                pollPaymentStatus(response.data.paymentId);
            }
        } catch (error) {
            alert('Payment failed: ' + (error.response?.data?.message || error.message));
        } finally {
            setLoading(false);
        }
    };

    const handleCardPayment = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            const response = await axios.post('http://localhost:5000/api/payments/card', {
                bookingId: bookingDetails.bookingId,
                amount: bookingDetails.totalAmount,
                email: cardData.email,
                name: cardData.name,
                cardDetails: {
                    cardNumber: cardData.cardNumber,
                    expiryMonth: cardData.expiryMonth,
                    expiryYear: cardData.expiryYear,
                    cvv: cardData.cvv
                }
            });

            if (response.data.requiresPin) {
                setRequiresPin(true);
                setFlwRef(response.data.flw_ref);
                alert(response.data.message);
            } else if (response.data.requiresOtp) {
                setRequiresOtp(true);
                setFlwRef(response.data.flw_ref);
                alert(response.data.message);
            } else if (response.data.success) {
                alert('Payment successful!');
                if (onPaymentSuccess) onPaymentSuccess(response.data);
            }
        } catch (error) {
            alert('Payment failed: ' + (error.response?.data?.message || error.message));
        } finally {
        setLoading(false);
        }
    };

    const handlePinValidation = async () => {
        setLoading(true);
        try {
            const response = await axios.post('http://localhost:5000/api/payments/validate-pin', {
                flw_ref: flwRef,
                pin: cardData.pin
            });

            if (response.data.success) {
                alert('Payment successful!');
                if (onPaymentSuccess) onPaymentSuccess(response.data);
            }
        } catch (error) {
            alert('PIN validation failed: ' + (error.response?.data?.message || error.message));
        } finally {
            setLoading(false);
        }
    };

    const handleOtpValidation = async () => {
        setLoading(true);
        try {
            const response = await axios.post('http://localhost:5000/api/payments/validate-otp', {
                flw_ref: flwRef,
                otp: cardData.otp
            });

            if (response.data.success) {
                alert('Payment successful!');
                if (onPaymentSuccess) onPaymentSuccess(response.data);
            }
        } catch (error) {
            alert('OTP validation failed: ' + (error.response?.data?.message || error.message));
        } finally {
            setLoading(false);
        }
    };

    const pollPaymentStatus = async (paymentId) => {
        const maxAttempts = 30;
        let attempts = 0;

        const interval = setInterval(async () => {
            attempts++;
            try {
                const response = await axios.get(`http://localhost:5000/api/payments/${paymentId}/status`);

                if (response.data.payment.status === 'completed') {
                    clearInterval(interval);
                    alert('Payment completed successfully!');
                    if (onPaymentSuccess) onPaymentSuccess(response.data.payment);
                } else if (response.data.payment.status === 'failed' || attempts >= maxAttempts) {
                    clearInterval(interval);
                    alert('Payment failed or timed out. Please try again.');
                }
            } catch (error) {
                console.error('Error polling payment status:', error);
            }
        }, 3000);
    };

    return (
        <div className="payment-form-container">
            <div className="payment-method-selector">
                <button
                    className={`method-btn ${paymentMethod === 'mobileMoney' ? 'active' : ''}`}
                    onClick={() => setPaymentMethod('mobileMoney')}
                >
                    ðŸ“± Mobile Money
                </button>
                <button
                    className={`method-btn ${paymentMethod === 'card' ? 'active' : ''}`}
                    onClick={() => setPaymentMethod('card')}
                >
                    ðŸ’³ Credit/Debit Card
                </button>
            </div>

            {paymentMethod === 'mobileMoney' && (
                <form onSubmit={handleMobileMoneyPayment} className="payment-form">
                    <h3>Pay with Mobile Money</h3>

                    <div className="form-group">
                        <label>Provider</label>
                        <select
                            value={mobileMoneyData.provider}
                            onChange={(e) => setMobileMoneyData({...mobileMoneyData, provider: e.target.value})}
                            required
                        >
                            <option value="MTN">MTN Mobile Money</option>
                            <option value="AIRTEL">Airtel Money</option>
                            <option value="ZAMTEL">Zamtel Kwacha</option>
                        </select>
                    </div>

                    <div className="form-group">
                        <label>Phone Number</label>
                        <input
                            type="tel"
                            placeholder="0977123456"
                            value={mobileMoneyData.phoneNumber}
                            onChange={(e) => setMobileMoneyData({...mobileMoneyData, phoneNumber: e.target.value})}
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label>Full Name</label>
                        <input
                            type="text"
                            placeholder="John Doe"
                            value={mobileMoneyData.name}
                            onChange={(e) => setMobileMoneyData({...mobileMoneyData, name: e.target.value})}
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label>Email</label>
                        <input
                            type="email"
                            placeholder="john@example.com"
                            value={mobileMoneyData.email}
                            onChange={(e) => setMobileMoneyData({...mobileMoneyData, email: e.target.value})}
                            required
                        />
                    </div>

                    <button type="submit" className="btn btn-primary" disabled={loading}>
                        {loading ? 'Processing...' : `Pay K${bookingDetails.totalAmount}`}
                    </button>
                </form>
            )}

            {paymentMethod === 'card' && !requiresPin && !requiresOtp && (
                <form onSubmit={handleCardPayment} className="payment-form">
                    <h3>Pay with Card</h3>

                    <div className="form-group">
                        <label>Card Number</label>
                        <input
                            type="text"
                            placeholder="1234 5678 9012 3456"
                            value={cardData.cardNumber}
                            onChange={(e) => setCardData({...cardData, cardNumber: e.target.value})}
                            maxLength="16"
                            required
                        />
                    </div>

                    <div className="form-row">
                        <div className="form-group">
                            <label>Expiry Month</label>
                            <input
                                type="text"
                                placeholder="MM"
                                value={cardData.expiryMonth}
                                onChange={(e) => setCardData({...cardData, expiryMonth: e.target.value})}
                                maxLength="2"
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Expiry Year</label>
                            <input
                                type="text"
                                placeholder="YY"
                                value={cardData.expiryYear}
                                onChange={(e) => setCardData({...cardData, expiryYear: e.target.value})}
                                maxLength="2"
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>CVV</label>
                            <input
                                type="text"
                                placeholder="123"
                                value={cardData.cvv}
                                onChange={(e) => setCardData({...cardData, cvv: e.target.value})}
                                maxLength="3"
                                required
                            />
                        </div>
                    </div>

                    <div className="form-group">
                        <label>Cardholder Name</label>
                        <input
                            type="text"
                            placeholder="John Doe"
                            value={cardData.name}
                            onChange={(e) => setCardData({...cardData, name: e.target.value})}
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label>Email</label>
                        <input
                            type="email"
                            placeholder="john@example.com"
                            value={cardData.email}
                            onChange={(e) => setCardData({...cardData, email: e.target.value})}
                            required
                        />
                    </div>

                    <button type="submit" className="btn btn-primary" disabled={loading}>
                        {loading ? 'Processing...' : `Pay K${bookingDetails.totalAmount}`}
                    </button>
                </form>
            )}

            {requiresPin && (
                <div className="validation-form">
                    <h3>Enter Your Card PIN</h3>
                    <div className="form-group">
                        <input
                            type="password"
                            placeholder="Enter 4-digit PIN"
                            value={cardData.pin}
                            onChange={(e) => setCardData({...cardData, pin: e.target.value})}
                            maxLength="4"
                        />
                    </div>
                    <button onClick={handlePinValidation} className="btn btn-primary" disabled={loading}>
                        {loading ? 'Validating...' : 'Submit PIN'}
                    </button>
                </div>
            )}

            {requiresOtp && (
                <div className="validation-form">
                    <h3>Enter OTP</h3>
                    <p>An OTP has been sent to your phone</p>
                    <div className="form-group">
                        <input
                            type="text"
                            placeholder="Enter OTP"
                            value={cardData.otp}
                            onChange={(e) => setCardData({...cardData, otp: e.target.value})}
                        />
                    </div>
                    <button onClick={handleOtpValidation} className="btn btn-primary" disabled={loading}>
                        {loading ? 'Validating...' : 'Submit OTP'}
                    </button>
                </div>
            )}
        </div>
    );
};

export default PaymentForm;
