import React, { useState, useEffect } from 'react';
import './App.css';
import SeatSelection from './components/Booking/SeatSelection';
import PaymentForm from './components/Payment/PaymentForm';

function App() {
  const [routes, setRoutes] = useState([]);
  const [buses, setBuses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState('home');
  const [selectedRoute, setSelectedRoute] = useState(null);
  const [selectedBus, setSelectedBus] = useState(null);
  const [selectedSeats, setSelectedSeats] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [routesResponse, busesResponse] = await Promise.all([
        fetch('http://localhost:5000/api/routes'),
        fetch('http://localhost:5000/api/buses')
      ]);

      const routesData = await routesResponse.json();
      const busesData = await busesResponse.json();

      setRoutes(routesData);
      setBuses(busesData);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRouteSelect = (route) => {
    setSelectedRoute(route);
    setCurrentPage('buses');
  };

  const handleBusSelect = (bus) => {
    setSelectedBus(bus);
    setCurrentPage('seats');
  };

  const handleSeatSelect = (seats) => {
    setSelectedSeats(seats);
    setCurrentPage('payment');
  };

  const renderHomePage = () => (
    <div>
      <section className="hero-section">
        <h2>Welcome to Zambian Bus Booking System</h2>
        <p className="hero-text">
          Skip the long queues at bus stations! Book your bus tickets online 
          and travel comfortably across Zambia.
        </p>
      </section>

      <section className="features-section">
        <h3>Why Choose Our Bus Booking System?</h3>
        <div className="features-grid">
          <div className="feature-card">
            <h4>üïê Save Time</h4>
            <p>No more waiting in long queues</p>
          </div>
          <div className="feature-card">
            <h4>üí∫ Choose Your Seat</h4>
            <p>Select your preferred seat</p>
          </div>
          <div className="feature-card">
            <h4>üì± Mobile Money</h4>
            <p>Pay with Mobile Money or card</p>
          </div>
          <div className="feature-card">
            <h4>üé´ Instant Tickets</h4>
            <p>Get tickets immediately</p>
          </div>
        </div>
      </section>

      <section className="routes-section">
        <h3>Available Routes - Click to Book</h3>
        {loading ? (
          <p>Loading routes...</p>
        ) : routes.length > 0 ? (
          <div className="routes-grid">
            {routes.map((route) => (
              <div 
                key={route._id} 
                className="route-card clickable" 
                onClick={() => handleRouteSelect(route)}
              >
                <h4>{route.startLocation} ‚Üí {route.destination}</h4>
                <div className="route-details">
                  <span>üìç {route.distance} km</span>
                  <span>‚è±Ô∏è {route.duration}</span>
                </div>
                <button className="btn btn-primary">
                  Book This Route
                </button>
              </div>
            ))}
          </div>
        ) : (
          <p>No routes available</p>
        )}
      </section>

      {buses.length > 0 && (
        <section className="stats-section">
          <h3>Our Fleet</h3>
          <div className="stats">
            <div className="stat-item">
              <span className="stat-number">{buses.length}</span>
              <span className="stat-label">Active Buses</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{routes.length}</span>
              <span className="stat-label">Routes</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{buses.reduce((total, bus) => total + bus.capacity, 0)}</span>
              <span className="stat-label">Total Seats</span>
            </div>
          </div>
        </section>
      )}
    </div>
  );

  const renderBusesPage = () => (
    <div className="buses-page">
      <div className="page-header">
        <button onClick={() => setCurrentPage('home')} className="btn btn-outline">
          ‚Üê Back to Routes
        </button>
        <h2>Available Buses</h2>
        <div className="route-info">
          <h3>{selectedRoute.startLocation} ‚Üí {selectedRoute.destination}</h3>
          <p>Distance: {selectedRoute.distance} km | Duration: {selectedRoute.duration}</p>
        </div>
      </div>

      <div className="buses-list">
        {buses.map((bus) => (
          <div key={bus._id} className="bus-card">
            <div className="bus-details">
              <h4>Bus {bus.busNumber}</h4>
              <div className="bus-info">
                <span>üöå Capacity: {bus.capacity} seats</span>
                <span>üí∫ Available: {bus.availableSeats} seats</span>
                <span>üí∞ Price: K150 per seat</span>
              </div>
            </div>
            <button className="btn btn-primary" onClick={() => handleBusSelect(bus)}>
              Select Seats
            </button>
          </div>
        ))}
      </div>
    </div>
  );

  const renderSeatSelection = () => (
    <SeatSelection
      busId={selectedBus._id}
      onSeatSelect={handleSeatSelect}
      onBack={() => setCurrentPage('buses')}
    />
  );

  const renderPayment = () => (
    <div className="payment-container">
      <h2>Complete Your Booking</h2>
      <div className="booking-summary">
        <p>Route: {selectedRoute.startLocation} ‚Üí {selectedRoute.destination}</p>
        <p>Bus: {selectedBus.busNumber}</p>
        <p>Seats: {selectedSeats.join(', ')}</p>
        <p>Total: K{selectedSeats.length * 150}</p>
      </div>
      <PaymentForm bookingId="temp-booking-id" />
    </div>
  );

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <h1 onClick={() => setCurrentPage('home')} style={{cursor: 'pointer'}}>
            üöå Zambian Bus Booking System
          </h1>
          <nav className="nav-buttons">
            <button onClick={() => setCurrentPage('home')} className="nav-btn">
              Home
            </button>
          </nav>
        </div>
      </header>
      <main className="main-content">
        {currentPage === 'home' && renderHomePage()}
        {currentPage === 'buses' && renderBusesPage()}
        {currentPage === 'seats' && renderSeatSelection()}
        {currentPage === 'payment' && renderPayment()}
      </main>
    </div>
  );
}

export default App;
