import React, { useState, useEffect } from 'react';

const SeatSelection = ({ busId, onSeatSelect }) => {
    const [seats, setSeats] = useState([]);
    const [selectedSeat, setSelectedSeat] = useState(null);

    useEffect(() => {
        // Fetch seat availability from the backend
        const fetchSeats = async () => {
            const response = await fetch(`/api/buses/${busId}/seats`);
            const data = await response.json();
            setSeats(data);
        };

        fetchSeats();
    }, [busId]);

    const handleSeatClick = (seat) => {
        setSelectedSeat(seat);
        onSeatSelect(seat);
    };

    return (
        <div>
            <h2>Select Your Seat</h2>
            <div className="seat-selection">
                {seats.map((seat) => (
                    <button
                        key={seat.id}
                        className={`seat ${seat.isBooked ? 'booked' : ''} ${selectedSeat === seat.id ? 'selected' : ''}`}
                        onClick={() => !seat.isBooked && handleSeatClick(seat.id)}
                        disabled={seat.isBooked}
                    >
                        {seat.number}
                    </button>
                ))}
            </div>
        </div>
    );
};

export default SeatSelection;
