import React, { useState, useEffect } from 'react';
import './App.css';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import Home from './pages/Home';
import Dashboard from './pages/Dashboard';

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [currentPage, setCurrentPage] = useState('home');

  // Check if user is already logged in when app loads
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // You can verify token with backend here
      // For now, we'll assume valid token means logged in
      const userData = localStorage.getItem('userData');
      if (userData) {
        setCurrentUser(JSON.parse(userData));
      }
    }
  }, []);

  const handleLogin = (user, token) => {
    setCurrentUser(user);
    setCurrentPage('dashboard');
    localStorage.setItem('token', token);
    localStorage.setItem('userData', JSON.stringify(user));
  };

  const handleLogout = () => {
    setCurrentUser(null);
    setCurrentPage('home');
    localStorage.removeItem('token');
    localStorage.removeItem('userData');
  };

  const renderPage = () => {
    switch (currentPage) {
      case 'login':
        return <Login onLogin={handleLogin} setCurrentPage={setCurrentPage} />;
      case 'register':
        return <Register setCurrentPage={setCurrentPage} />;
      case 'dashboard':
        return <Dashboard user={currentUser} onLogout={handleLogout} setCurrentPage={setCurrentPage} />;
      default:
        return <Home setCurrentPage={setCurrentPage} currentUser={currentUser} onLogout={handleLogout} />;
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <h1 onClick={() => setCurrentPage('home')} style={{cursor: 'pointer'}}>
            ðŸšŒ Zambian Bus Booking System
          </h1>
          <nav className="nav-buttons">
            {currentUser ? (
              <div className="user-info">
                <span>Welcome, {currentUser.username}!</span>
                <button onClick={() => setCurrentPage('dashboard')} className="nav-btn">
                  Dashboard
                </button>
                <button onClick={handleLogout} className="logout-btn">
                  Logout
                </button>
              </div>
            ) : (
              <div className="auth-nav">
                <button onClick={() => setCurrentPage('login')} className="nav-btn">
                  Login
                </button>
                <button onClick={() => setCurrentPage('register')} className="nav-btn nav-btn-secondary">
                  Register
                </button>
              </div>
            )}
          </nav>
        </div>
      </header>
      <main>
        {renderPage()}
      </main>
    </div>
  );
}

export default App;
