import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BusSearch = () => {
    const [buses, setBuses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [searchParams, setSearchParams] = useState({
        startLocation: '',
        destination: '',
        travelDate: ''
    });

    useEffect(() => {
        const fetchBuses = async () => {
            try {
                const response = await axios.get('/api/buses', { params: searchParams });
                setBuses(response.data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        if (searchParams.startLocation && searchParams.destination && searchParams.travelDate) {
            fetchBuses();
        }
    }, [searchParams]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSearchParams({ ...searchParams, [name]: value });
    };

    const handleSearch = (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null);
    };

    return (
        <div>
            <h2>Search for Buses</h2>
            <form onSubmit={handleSearch}>
                <input
                    type="text"
                    name="startLocation"
                    placeholder="Start Location"
                    value={searchParams.startLocation}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="destination"
                    placeholder="Destination"
                    value={searchParams.destination}
                    onChange={handleChange}
                    required
                />
                <input
                    type="date"
                    name="travelDate"
                    value={searchParams.travelDate}
                    onChange={handleChange}
                    required
                />
                <button type="submit">Search</button>
            </form>
            {loading && <p>Loading...</p>}
            {error && <p>Error: {error}</p>}
            <ul>
                {buses.map(bus => (
                    <li key={bus.id}>
                        {bus.busNumber} - {bus.route.startLocation} to {bus.route.destination} - {bus.availableSeats} seats available
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default BusSearch;
