import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './SeatSelection.css';

const SeatSelection = ({ busId, onSeatSelect, onBack }) => {
    const [seats, setSeats] = useState([]);
    const [selectedSeats, setSelectedSeats] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchSeats();
    }, [busId]);

    const fetchSeats = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/api/buses/${busId}/seats`);
            setSeats(response.data);
        } catch (error) {
            console.error('Error fetching seats:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleSeatClick = (seatNumber) => {
        if (selectedSeats.includes(seatNumber)) {
            setSelectedSeats(selectedSeats.filter(s => s !== seatNumber));
        } else {
            setSelectedSeats([...selectedSeats, seatNumber]);
        }
    };

    const handleContinue = () => {
        if (selectedSeats.length > 0) {
            onSeatSelect(selectedSeats);
        } else {
            alert('Please select at least one seat');
        }
    };

    if (loading) return <div>Loading seats...</div>;

    return (
        <div className="seat-selection-container">
            <div className="seat-selection-header">
                <button onClick={onBack} className="btn btn-outline">‚Üê Back</button>
                <h2>Select Your Seats</h2>
                <p>Selected: {selectedSeats.length} seat(s)</p>
            </div>

            <div className="bus-layout">
                <div className="driver-section">
                    <div className="driver-seat">üöó Driver</div>
                </div>

                <div className="seats-grid">
                    {seats.map((seat) => (
                        <button
                            key={seat.id}
                            className={`seat ${seat.isBooked ? 'booked' : ''} ${
                                selectedSeats.includes(seat.number) ? 'selected' : ''
                            }`}
                            onClick={() => !seat.isBooked && handleSeatClick(seat.number)}
                            disabled={seat.isBooked}
                        >
                            {seat.number}
                        </button>
                    ))}
                </div>
            </div>

            <div className="seat-legend">
                <div className="legend-item">
                    <span className="legend-box available"></span>
                    <span>Available</span>
                </div>
                <div className="legend-item">
                    <span className="legend-box selected"></span>
                    <span>Selected</span>
                </div>
                <div className="legend-item">
                    <span className="legend-box booked"></span>
                    <span>Booked</span>
                </div>
            </div>

            <div className="continue-section">
                <button 
                    onClick={handleContinue} 
                    className="btn btn-primary"
                    disabled={selectedSeats.length === 0}
                >
                    Continue to Payment ({selectedSeats.length} seat{selectedSeats.length !== 1 ? 's' : ''})
                </button>
            </div>
        </div>
    );
};

export default SeatSelection;
